import "./DescriptionBox.css";

function DescriptionBox(props) {
  let box = props.item.description_box;

  let title_style = {
    color: box.style.title.color,
    display: box.style.title.display,
  };

  return (
    <div className={box.position}>
      <h1 style={title_style}>{box.value.title}</h1>
      <p style={{ color: box.style.description_color }}>
        {box.value.ingridients}
      </p>
      <h6>{box.value.alergies}</h6>
      <h2>
        Доручек: За едно лице {box.value.price}{box.value.currency} За две лица 700ден.
      </h2>
    </div>
  );
}

export default DescriptionBox;
